class UsersController < ApplicationController

  def index
  end

  def create
    token = params[:jwt]
    rsa_public = <<DOC
-----BEGIN CERTIFICATE-----

-----END CERTIFICATE-----
DOC
    rsa = OpenSSL::X509::Certificate.new(rsa_public)
    decoded_token = JWT.decode token, rsa.public_key, true, algorithm: "RS256"
  end

end
